import Layout from '../components/PageLayout';
import { CardGroup, Card, Image, Button, Carousel, Container, Row, Col } from 'react-bootstrap';
import { TwitchEmbed, TwitchChat, TwitchClip, TwitchPlayer } from 'react-twitch-embed';
import { TwitterTimelineEmbed, TwitterShareButton, TwitterFollowButton, TwitterHashtagButton, TwitterMentionButton, TwitterTweetEmbed, TwitterMomentShare, TwitterDMButton, TwitterVideoEmbed, TwitterOnAirButton } from 'react-twitter-embed';
import ScrollAnimation from 'react-animate-on-scroll';
import React from 'react';
import ReactPlayer from 'react-player';



export default class Index extends React.Component {

  constructor(props) {
      super(props)
      this.state ={
          url: "https://player.twitch.tv/shift"
      }
  }

  load = url => {
    this.setState({
      url
    })
  }

 render() {
   return (
    <Layout>
    <title>Shift: Live 8pm EST!</title>
    <div style={{ paddingRight: 15, paddingLeft: 15 }}>

      <div class="row" style={{ height: 700 }}>
        <div class="column" style={{ width: "15%" }}>
          <ScrollAnimation animateIn="fadeInLeft" animateOnce="true" offset="50" delay="500">
            <TwitterTimelineEmbed
              sourceType="profile"
              screenName="shiftposting"
              theme="dark"
              options={{ height: 700 }}
            />
          </ScrollAnimation>
        </div>
        <div class="column" style={{ width: "65%" }}>
          <ScrollAnimation animateIn="fadeInDown" animateOnce="true" offset="50" delay="1000">
            <div className='player-wrapper'>
              <ReactPlayer
                className='react-player'
                url={this.state.url}
                width='100%'
                height='700px'
                controls="true"
              />
            </div>
          </ScrollAnimation>
        </div>
        <div class="column" style={{ width: "20%" }}>
          <ScrollAnimation animateIn="fadeInRight" animateOnce="true" offset="50" delay="500">
            <TwitchChat channel="shift" width="100%" height='700px' theme="dark" parent={['localhost']} style={{ border: 0 }} />
          </ScrollAnimation>
        </div>
      </div>
    </div>

    <Button onClick={()=> this.setState({url:"https://www.youtube.com/watch?v=ekMVSQBlSOo"})}>
      Change
    </Button>

    <style>
      {`
        .column {
          float: left;
          height: 100%;
        }

      `}
    </style>

  </Layout>
   )
 }
}



