import { Tooltip, OverlayTrigger } from 'react-bootstrap';

const renderTooltip = (props) => (
  <Tooltip id="button-tooltip" {...props}>
    {props.name}
  </Tooltip>
);

export default class BlitzGrid extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
    }
  }

  onClick(){

    let data = { pid: this.props.pid, rid: this.props.rid, name: this.props.name }

    let options = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(data),
    }


    fetch('http://localhost:3001/blitz/joinroom', options)
      .then(res => res.json())
      .then(data => {
        this.setState({ room: data })
        //Get new room data
        const interval = setInterval(() => {
          this.refreshRoom();
        }, 1000);
      });

  }

  render() {
    return (
      <div>
        <OverlayTrigger
          placement="right"
          delay={{ show: 100, hide: 400 }}
          overlay={renderTooltip(this.props)}>

          <div class="butt" onClick={()=>this.onClick()} style={{ backgroundImage: ('url("' + this.props.type + '/' + this.props.name + '.png' + '")') }}>
          </div>

        </OverlayTrigger>
        <style>{`
          .butt {
            border: 1px outset white;
            padding-top:100%;
            cursor:pointer;
            width:100%
            -webkit-background-size: cover;
            -moz-background-size: cover;
            -o-background-size: cover;
            background-size: cover;
            background-blend-mode: multiply;
         }

         .butt:hover {
            background-color: red;
            color:white;
         }

          `}</style>
      </div>
    )
  }

}
