import { useRouter } from 'next/router'
import fetch from 'node-fetch'
import PersonInfo from '../../components/PersonInfo.js'
import { DropdownButton, Dropdown, Button } from 'react-bootstrap'
import PageLayout from '../../components/PageLayout.js'
import BACKEND_URL from '../../backendurl.js'

export default class Index extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      level:null
    }

  }

  static getInitialProps({ query }) {
    return { query }
  }

  componentDidMount() {

    this.getLevelData();

  }

  getLevelData(){

  }


  render() {
    if (this.state.level == null)
      return (<div style={{ color: "#aabfff" }}>Loading level {this.props.query.id}...</div>)

    return (
      <PageLayout>
      <div style={{ color: "white", padding: 20 }}>

        <div class="row">
          <div class="column">
            <h1>CCN {this.state.post.crash[0].ccn}</h1>
            <div style={{ color: "grey" }}>{this.state.post.crash[0].collision_type}, {this.state.post.crash[0].crash_date}</div>
          </div>
          <div class="column"></div>
          <div class="column">
            <div style={{float:"right"}}>
              {this.state.bookmarked == false &&
                <Button style={{display:"inline" }} onClick={() => this.BookmarkCCN(this.state.post.crash[0].ccn, "add")} variant="outline-primary">Bookmark</Button>
              }
              {this.state.bookmarked == true &&
                <Button style={{display:"inline" }} onClick={() => this.BookmarkCCN(this.state.post.crash[0].ccn, "remove")} variant="primary">Bookmarked</Button>
              }

              <DropdownButton style={{display:"inline",paddingLeft:5 }} id ="dropdown-basic-button" title="Bookmarks">
                  {this.state.bookmarks.map(bookmark =>
                    <Dropdown.Item href={'/ccn/' + bookmark.ccn}>{bookmark.ccn}</Dropdown.Item>
                  )}
              </DropdownButton>
            </div>

          </div>


        </div>


        <div class="row">
          <div class="column">
            <h2>Crash info</h2>
            <p>{this.state.post.crash[0].DETAILEDNARRATIVE}</p>
          </div>
          <div class="column">
            <h2>People involved</h2>
            {this.state.post.persons.map(person =>
              <PersonInfo
                personInfo={person}
              />
            )}
          </div>
          <div class="column">
            <h2>Vehicles involved</h2>

          </div>
        </div>



        <style>
          {`
      .column {
        float: left;
        width: 33.33%;
        padding: 20px;
        }

      /* Clear floats after the columns */
      .row:after {
        content: "";
        display: table;
        clear: both;

      }`
          }
        </style>

      </div>
      </PageLayout>
    )
  }

}
