import React from 'react';
import axios from 'axios';
import BattleShipGrid from '../components/BattleShipGrid.js'
import { Button, InputGroup, FormControl,Container } from 'react-bootstrap'

export default class Index extends React.Component {


  constructor(props) {
    super(props)
    this.state = {
      board: { tasks: [] },
      room: null,
      player_id: ""
    }
  }

  componentDidMount() {
    //Give the player an id
    this.setState({ player_id: [...Array(16)].map(i => (~~(Math.random() * 36)).toString(36)).join('') });

  }

  createRoomAndJoin() {

    let data = {pid:this.state.player_id}

    let options= {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(data),
      }


    fetch('http://localhost:3001/mainThread/createroom',options)
      .then(res => res.json())
      .then(data => {
        this.setState({ room: data })
      });
  }

  joinRoom(rid){
    let data = {pid:this.state.player_id,rid:rid}

    let options= {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(data),
      }


    fetch('http://localhost:3001/mainThread/joinroom',options)
      .then(res => res.json())
      .then(data => {
        this.setState({ room: data })
      });
  }


  render() {
    return (
      <div>

        {this.state.room == null &&
          <Container>
            <h1>
              Welcome to BfBB Battleship!
            </h1>
            <InputGroup className="mb-3">
              <InputGroup.Prepend>
                <InputGroup.Text id="basic-addon1">Display name</InputGroup.Text>
              </InputGroup.Prepend>
              <FormControl
                placeholder="ie: John"
                aria-label="ie: John"
                aria-describedby="basic-addon1"
              />
            </InputGroup>
            <br></br>
            <Button variant="outline-primary" style={{width:"100%"}} onClick={() => this.createRoomAndJoin()}>Create Room</Button>
            <br></br><br></br>
            <InputGroup className="mb-3">

              <FormControl
                placeholder="Room ID"
                aria-label="Recipient's username"
                aria-describedby="basic-addon2"
              />
              <InputGroup.Append>
                <Button variant="outline-primary">Join Room</Button>
              </InputGroup.Append>
            </InputGroup>
          </Container>}
        {this.state.room != null &&
          <div>
            RoomID: {this.state.room.id}
            <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr 1fr 1fr 1fr 1fr 1fr 1fr 1fr 1fr 1fr", paddingBottom: "100%", width: 800, justifyContent: 'space-between' }}>
              {this.state.room.player1.board.tasks.map(task =>
                <BattleShipGrid name={task.name} type={task.type} />
              )}
            </div>
          </div>}




      </div>

    )
  }
}
