import { useRouter } from 'next/router'
import fetch from 'node-fetch'
import { DropdownButton, Dropdown, Button, Container,Image } from 'react-bootstrap'
import PageLayout from '../../components/PageLayout.js'
import BACKEND_URL from '../../backendurl.js'

export default class Index extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      level: null
    }

  }

  static getInitialProps({ query }) {
    return { query }
  }

  componentDidMount() {

    this.getLevelData();

  }

  getLevelData() {


    fetch(BACKEND_URL + 'bfbb-site/level/' + this.props.query.id)
      .then(res => res.json())
      .then(data => {
        console.log(data)
        if (data.success == true && data.info.length > 0)
          this.setState({ level: data.info[0] })
      });
  }


  render() {
    if (this.state.level == null)
      return (<div style={{ color: "#aabfff" }}>Loading level {this.props.query.id}...</div>)

    return (
      <PageLayout>

        <Container>
        <Image style={{borderRadius:25}} src={"/level-banners/"+this.state.level.name+".png"} fluid></Image>
          <h1>
            {this.state.level.name}
          </h1>
          <p>
            {this.state.level.description}
          </p>
          <div>{this.state.level.secondary_character}</div>
          <div>{this.state.level.sock_count}</div>
          <div>{this.state.level.spatula_requirement}</div>

        </Container>

        <style>{`
          body{
            background-image: url("/level-backgrounds/`+ this.state.level.name + `.png");
            background-repeat: no-repeat;
            background-attachment: fixed;
          }
        `}</style>
      </PageLayout>
    )
  }

}
