import { Component } from 'react';
import ReactMapGL from 'react-map-gl';

class Map extends Component {

  state = {
    viewport: {
      width: '100vw',
      height: '100vh',
      latitude: 38.9868,
      longitude: -77.0325,
      zoom: 13,
    },
    geojson: {}
  };

  componentDidMount() {

    const map = this.reactMap.getMap(); 

    map.on('load', () => {
        fetch('http://localhost:3001/taras/coordinates/geojson')
        .then(res => res.json())
        .then(data => this.displayDataOnMap({
            "type": "geojson",
            "data": {
            "type": "Feature",
            "geometry": {
            "type": "Point",
            "coordinates": [-77.0323, 38.9131]
            },
            "properties": {
            "title": "Mapbox DC",
            "marker-symbol": "monument"
            }
            }}));
    })


  }

  displayDataOnMap(geojson){

    const map = this.reactMap.getMap(); 

    map.addSource('crash-data',geojson)

    map.addLayer({
        'id': 'points',
        'type': 'circle',
        'source': 'crash-data',
        'paint': {
            'fill-color': '#f08',
            'fill-opacity': 0.4
            },
        'layout': {}});


  }

  render() {
    return (
      <ReactMapGL
        ref={(reactMap) => this.reactMap = reactMap}
        mapStyle="mapbox://styles/mapbox/streets-v9"
        mapboxApiAccessToken="pk.eyJ1IjoieW9tYW40NyIsImEiOiJjampxM2g3YjUyMzFyM3ZvMXNjZ2R3d2ZtIn0.r0zXd8hAqa1haG3lkjlDwA"
        onViewportChange={(viewport) => this.setState({ viewport })}
        {...this.state.viewport}
      />
    );
  }
}

export default Map;
