import Layout from '../components/PageLayout';
import PlainText from '../components/PlainText';
import { TwitterTimelineEmbed, TwitterShareButton, TwitterFollowButton, TwitterHashtagButton, TwitterMentionButton, TwitterTweetEmbed, TwitterMomentShare, TwitterDMButton, TwitterVideoEmbed, TwitterOnAirButton } from 'react-twitter-embed';


const Index = () => (
  <Layout>
    <iframe src="https://player.twitch.tv/?channel=bfbbcommunity" frameborder="0" allowfullscreen="true" scrolling="no" height="378" width="620"></iframe>
    <div style={{positon:'fixed',right:20}}>
      <TwitterTimelineEmbed
        sourceType="profile"
        screenName="BfBBCommunity"
        options={{height: 400, width: 400}}
      />
      <TwitterHashtagButton
      tag={'bfbb'}
      />
      <TwitterFollowButton
      screenName={'BfBBComunity'}
      />  
    </div>

  </Layout>
);

export default Index;
