import { Tooltip, OverlayTrigger } from 'react-bootstrap';

const renderTooltip = (props) => (
  <Tooltip id="button-tooltip" {...props}>
    {props.name}
  </Tooltip>
);

export default class BlitzGrid extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      collected: false,
    }
  }

  componentDidMount() {
    this.setState({collected:this.props.collected});
  }

  onClick() {

    let data = { pid: this.props.pid, rid: this.props.rid, collectable_name: this.props.name }

    let options = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(data),
    }


    fetch('http://localhost:3001/blitz/collect', options)
      .then(res => res.json())
      .then(data => {
        this.setState({ room: data })
        if (data.success)
          this.setState({collected:true});
      });

  }

  render() {
    return (
      <div>
        <OverlayTrigger
          placement="right"
          delay={{ show: 100, hide: 400 }}
          overlay={renderTooltip(this.props)}>
          <div>
            {!this.props.collected &&
              <div class="butt" onClick={() => this.onClick()} style={{ backgroundImage: ('url("' + this.props.type + '/' + this.props.name + '.png' + '")') }} />
            }

            {this.props.collected &&
              <div class="butt" onClick={() => this.onClick()} style={{ backgroundColor: "red", backgroundImage: ('url("' + this.props.type + '/' + this.props.name + '.png' + '")') }} />
            }
          </div>



        </OverlayTrigger>
        <style>{`
          .butt {
            border: 1px outset white;
            padding-top:100%;
            cursor:pointer;
            width:100%
            -webkit-background-size: cover;
            -moz-background-size: cover;
            -o-background-size: cover;
            background-size: cover;
            background-blend-mode: multiply;
         }

         .butt:hover {
            background-color: grey;
            color:white;
         }



          `}</style>
      </div>
    )
  }

}
