import fetch from 'node-fetch'
import PersonInfo from '../../components/PersonInfo.js'
import { DropdownButton, Dropdown, Button } from 'react-bootstrap'
import data from '../../../../bfbb-backend/routes/data.js';

//const BACKEND_IP = "https://bfbbhub.herokuapp.com";
const BACKEND_IP = "http://172.16.127.245:3001";

export default class Index extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      room:null,
    }

  }

  static getInitialProps({ query }) {
    return { query }
  }

  componentDidMount() {


    fetch(backend_url+'/blitz/getroom/' + this.props.query.id, options)
      .then(res => res.json())
      .then(data => {
        this.setState({room:data})
      });

  }


  render() {
    if (this.state.room == null)
      return (<div style={{ color: "#aabfff" }}>Loading room {this.props.query.id}...</div>)

    return (
      <div>
        found room :D
      </div>
    )
  }

}
