import { Row, Col } from 'react-bootstrap';
import BlitzGrid from '../components/BlitzGrid.js'

const BlitzBoard = (props) => (
    <div>
        Room ID: {props.room.id}
        <Row style={{ width: "100%", textAlign:"center" }}>
            <Col>
                <h1>{props.room.player1.display_name}</h1>
                <h2>{props.room.player1.score}</h2>
            </Col>
            <Col>
                {props.room.player2.id == null && <>
                    <h1>Use the following room code to invite a friend:</h1>
                    <h2>{props.room.id}</h2></>
                }

                {props.room.player2.id != null && <>
                    <h1>{props.room.player2.display_name}</h1>
                    <h2>{props.room.player2.score}</h2></>
                }
            </Col>
        </Row>
        <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr 1fr 1fr 1fr 1fr 1fr 1fr 1fr 1fr 1fr 1fr 1fr 1fr 1fr 1fr 1fr 1fr 1fr 1fr", width: "100%", justifyContent: 'space-between' }}>
            {props.room.board.tasks.map(task =>
                <BlitzGrid rid={props.room.id} pid={props.pid} name={task.name} type={task.type} collected={task.collected} />
            )}
        </div>
    </div>


);

export default BlitzBoard;
