import React from 'react';
import axios from 'axios';
import BattleShipGrid from '../components/BattleShipGrid.js'

export default class Index extends React.Component {


  constructor(props) {
    super(props)
    this.state = {
      board: {tasks:[]}
    }
  }

  componentDidMount(){
    fetch('http://localhost:3001/mainThread/createboard')
    .then(res => res.json())
    .then(data => {
      this.setState({ board: data })
    });
  }


  render() {
    return (
      <div>
        Board here
        <div style={{display:"grid",gridTemplateColumns:"auto auto auto auto auto auto auto",paddingBottom:"100%"}}>
          {this.state.board.tasks.map(task=>
              <BattleShipGrid name={task.name} />
              )}
        </div>



      </div>

    )
  }
}
