const BattleShipGrid = (props) => (
    <div>
        <div class="butt">
            <div class="content">
                {props.name}
            </div>
        </div>
        <style>{`
          .butt {
            border: 1px outset blue;
            background-color: lightBlue;
            cursor:pointer;
            width:100%

            position: relative;
  width: 20%;
         }

         .butt:hover {
            background-color: blue;
            color:white;
         }

         .content {
            position: absolute;
            top: 0;
            left: 0;
            bottom: 0;
            right: 0;
            background: #333;
            color: #fff;
          /*   display: flex;
            align-items: center; */
            line-height:100%;
            height:100%;
            text-align: center;
            display: flex;
            align-items: center;
            justify-content: center;
          }
          `}</style>
    </div>

);

export default BattleShipGrid;
