import React from 'react';
import axios from 'axios';
import dynamic from 'next/dynamic';
import { Button } from 'react-bootstrap'

export default class Index extends React.Component {

  getCoords = 'http://localhost:3001/taras/coordinates';


  constructor(props) {
    super(props)
    this.state = {
      username: "",
      password: "",
      login_response:{},
      login_message:""
    }

  }

  login() {

    let data = { username: this.state.username, password: this.state.password }

    let options = {
      method: 'post',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(data)
    }

    fetch('http://localhost:3001/crash/login',options)
    .then(res => res.json())
    .then(data => 
      this.setState({ login_response: data })

    );

  }

  render() {
    return (
      <div>
        <div style={{ width: 300, height: 200, backgroundColor: "white", position: "fixed", borderRadius: 20, textAlign: "center" }}>
          <h1>
            Login
          </h1>
          <input type="email" onChange={event => this.setState({ username: event.target.value })}></input>
          <input type="password" onChange={event => this.setState({ password: event.target.value })}></input>
          <br></br>
          <Button value="Login" onClick={() => this.login()}>Login</Button>
          <div>
            {this.state.login_message}
          </div>
        </div>
      </div>
    )
  }
}
