import Layout from '../components/PageLayout';
import { CardGroup, Card, Image, Button, Carousel, Container, Row, Col } from 'react-bootstrap';
import ScrollAnimation from 'react-animate-on-scroll';
import React from 'react'
import ReactPlayer from 'react-player';

export default class Index extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      url: "https://player.twitch.tv/shift",
      playing: true,
      videos: []
    }

    this.changeVideo = this.changeVideo.bind(this);
  }

  render() {
    return (
      <Layout>
        <title>The History of BfBB</title>
        <h1 style={{ color: "white", textAlign: "center" }}>How Speedrunning Revived BfBB</h1>

        <ScrollAnimation animateIn="bounceInLeft" animateOnce="true" offset="50">
          <div style={{ width: '100%', paddingLeft: '20%', paddingRight: '20%', height: '34vw' }}>
            <iframe width="100%" height="100%" src="https://www.youtube.com/embed/videoseries?list=PLDzuZShnnVbZ78eM3wnnXq1C3mnU2PMZf" frameborder="0" allow="autoplay; encrypted-media; fullscreen" allowfullscreen></iframe>
          </div>
        </ScrollAnimation>

        <div style={{ width: '100%', paddingLeft: 40, paddingRight: 40, paddingTop:20 }} >
          <h3 style={{ color: "white" }}>
            Catch the latest:
          </h3>
          <Row style={{ width: '100%' }}>
            {this.state.videos.map(vid =>
              <Col style={{ padding: 0, textAlign: 'center' }}>
                <VideoThumbnail
                  videoId={vid.id.videoId}
                  title={vid.snippet.title}
                  thumbnail={vid.snippet.thumbnails.medium.url}
                  changeVideo={this.changeVideo}
                >
                </VideoThumbnail>
              </Col>
            )}
          </Row>
        </div>

      </Layout>
    )
  }
}
