import { useRouter } from 'next/router'
import fetch from 'node-fetch'
import { DropdownButton, Dropdown, Button, Container, Image, Badge, Card, Accordion } from 'react-bootstrap'
import BACKEND_URL from './backendurl.js'
import ScrollAnimation from 'react-animate-on-scroll';

export default class AddArea extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            levels: [],
            level: "",
            name: "",
            internal_name: "",
            sock_count: 0,
            spatula_count: 0
        }
    }

    componentDidMount() {
        this.getLevels();
    }

    getLevels() {
        {

            fetch(BACKEND_URL + 'bfbb-site/levels')
                .then(res => res.json())

                .then(data => {
                    if (data.success == true) {
                        this.setState({ levels: data.info })
                    }
                })


        }
    }


    render() {
        return (<div>
            <Dropdown>
                <Dropdown.Toggle variant="success" id="dropdown-basic">
                    {this.state.level}
                </Dropdown.Toggle>

                <Dropdown.Menu>
                    {this.state.levels.map(level=>
                        <Dropdown.Item href="#/action-1">{level.name}</Dropdown.Item>
                        )}
                </Dropdown.Menu>
            </Dropdown>
        </div>);
    }
}
