import React from 'react';
import axios from 'axios';
import BattleShipGrid from '../components/BattleShipGrid.js'

export default class Index extends React.Component {


  constructor(props) {
    super(props)
    this.state = {
      board: {}
    }
  }

  componentDidMount(){
    fetch('http://localhost:3001/mainThread/createboard')
    .then(res => res.json())
    .then(data => {
      this.setState({ board: data })
    });
  }


  render() {
    return (
      <div>
        Board here
        {this.state.board.tasks.map(message=>
            <Message sender = {message.sender} message={message.message} time={message.time} messageID={message._id} ></Message>
            )}
      </div>

    )
  }
}
