import { Row, Col } from 'react-bootstrap';
import BlitzGrid from '../components/BlitzGrid.js'

const BlitzBoard = (props) => (
    <div>
        <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr 1fr 1fr 1fr 1fr 1fr 1fr 1fr 1fr 1fr", width: 750, justifyContent: 'space-between' }}>
            {props.board.tasks.map(task =>
                <BlitzGrid name={task.name} type={task.type} />
            )}
        </div>
    </div>


);

export default BlitzBoard;
