const buttonStyle = {
  width:'40px',
  height:'40px',
  padding:'0px'
};

const BattleShipGrid = (props) => (
    <div>
        <div style={{buttonStyle}} class="butt">{props.name}</div>
        <style>{`
          .butt {
            border: 1px outset blue;
            background-color: lightBlue;
            padding-top:100%;
            cursor:pointer;
         }

         .butt:hover {
            background-color: blue;
            color:white;
         }
          `}</style>
    </div>

);

export default BattleShipGrid;
