import React from 'react';
import axios from 'axios';
import PageLayout from '../components/PageLayout.js'
import { Button, InputGroup, FormControl, Container, Col, Row, Image, Table } from 'react-bootstrap'
import BACKEND_URL from '../components/backendurl.js'

const information_container = { backgroundColor: "#111111aa", borderRadius: 20, filter: "drop-shadow(0 0 0.75rem black)", padding: 20, paddingBottom: 10, marginTop: 10 }

const button_style = {
  width: "100%",
  marginTop: 20
}

export default class Index extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      authorized: false,
      username: "",
      password: "",
      token: "",
      levels: [],
      areas: [],
      strats: [],
    }
  }

  componentDidMount() {
    this.setState({username:window.sessionStorage.getItem("username"),token:window.sessionStorage.getItem("token")})
  }

  getData() {
    fetch(BACKEND_URL + 'bfbb-site/levels')
      .then(res => res.json())
      .then(data => {
        if (data.success == true)
          this.setState({ levels: data.info })
      });

    fetch(BACKEND_URL + 'bfbb-site/areas')
      .then(res => res.json())
      .then(data => {
        if (data.success == true)
          this.setState({ areas: data.info })
      });

    fetch(BACKEND_URL + 'bfbb-site/strats')
      .then(res => res.json())
      .then(data => {
        if (data.success == true)
          this.setState({ strats: data.info })
      });
  }

  login() {
    let data = { username: this.state.username, password: this.state.password }

    let options = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(data),
    }


    fetch(BACKEND_URL + 'bfbb-site/login', options)
      .then(res => res.json())
      .then(data => {
        if (data.success) {
          this.setState({ authorized: true, token: data.token })

          window.sessionStorage.setItem("username",this.state.username)
          window.sessionStorage.setItem("token",data.token)

          this.getData()
        } else {
          console.log(data.error)
        }
      });
  }


  render() {
    return (
      <PageLayout>
        <title>&#128680;Admin Console&#128680;</title>


        <Container style={{ textAlign: "Center" }}>

          {!this.state.authorized ?
            <div style={information_container}>
              <h1>
                Prove you belong here.
              </h1>
              <FormControl
                onChange={(e) => this.setState({ username: e.target.value })}
                placeholder="Username"
                aria-label="Username"
                aria-describedby="basic-addon1"
              />
              <FormControl
                onChange={(e) => this.setState({ password: e.target.value })}
                placeholder="Password"
                aria-label="Password"
                aria-describedby="basic-addon1"
              />
              <Button onClick={() => this.login()}>Verify Me</Button>
            </div>

            :
            <>
              <div style={information_container}>
                Levels
                <Table variant="dark" striped bordered hover size="sm">
                  <thead>
                    <tr>
                      <th>Name</th>
                      <th>Description</th>
                      <th>Sock Count</th>
                      <th>Spatula Requirement</th>
                      <th>Secondary Character</th>
                    </tr>
                  </thead>
                  <tbody>
                    {this.state.levels.map(level =>
                      <tr>
                        <td>{level.name}</td>
                        <td>{level.description}</td>
                        <td>{level.sock_count}</td>
                        <td>{level.spatula_requirement}</td>
                        <td>{level.secondary_character}</td>
                      </tr>
                    )}
                  </tbody>
                </Table>
              </div>

              <div style={information_container}>
                Areas
                <Table variant="dark" striped bordered hover size="sm">
                  <thead>
                    <tr>
                      <th>Level</th>
                      <th>Name</th>
                      <th>Internal Name</th>
                      <th>Sock Count</th>
                      <th>Spatula Count</th>
                    </tr>
                  </thead>
                  <tbody>
                    {this.state.areas.map(area =>
                      <tr>
                        <td>{area.level}</td>
                        <td>{area.name}</td>
                        <td>{area.internal_name}</td>
                        <td>{area.sock_count}</td>
                        <td>{area.spatula_count}</td>
                      </tr>
                    )}
                  </tbody>
                </Table>
              </div>


              <div style={information_container}>
                Strats
              <Table variant="dark" striped bordered hover size="sm">
                <thead>
                  <tr>
                    <th>Name</th>
                    <th>Description</th>
                    <th>Methods</th>
                    <th>Area</th>
                  </tr>
                </thead>
                <tbody>
                  {this.state.strats.map(strat =>
                    <tr>
                      <td>{strat.name}</td>
                      <td>{strat.description}</td>
                      <td>Methods here</td>
                      <td>{strat.area}</td>
                    </tr>
                  )}
                </tbody>
              </Table>
            </div>
            </>
          }


        </Container>


      <style>{`body{
        background-image: url("police.png");
        background-repeat: no-repeat;
        background-attachment: fixed;
      }`}</style>
      </PageLayout >


    )
  }
}
