import { Component } from 'react';
import ReactMapGL from 'react-map-gl';

class Map extends Component {

  state = {
    viewport: {
      width: '100vw',
      height: '100vh',
      latitude: 38.9868,
      longitude: -77.0325,
      zoom: 13,
    },
    geojson: {}
  };

  componentDidMount() {

    const map = this.reactMap.getMap(); 
    this.pullNewData(0);
  }

  pullNewData(chunk){

    let url = 'http://localhost:3001/taras/coordinates/geojson?chunk=' + chunk;

    map.on('load', () => {
      fetch()
      .then(res => res.json(url))
      .then(data => this.displayDataOnMap(data.data,chunk));
  })

  }

  displayDataOnMap(geojson,chunk){

    const map = this.reactMap.getMap(); 

    // add initial chunk of data
    if(chunk == 0){

      this.state.geojson = geojson;

      map.addSource('crash-data',this.state.geojson)

      map.addLayer(
        {
        'id': 'crash-heat',
        'type': 'heatmap',
        'source': 'crash-data',
        });

      map.addLayer({
          'id': 'points',
          'type': 'circle',
          'source': 'crash-data',
          'layout': {}});
    } else {

      Array.prototype.push.apply(this.state.geojson.data.features, geojson.data.features);

      map.getSource('crash-data').setData(this.state.geojson)


    }

    this.pullNewData(chunk+1);

  }

  render() {
    return (
      <ReactMapGL
        ref={(reactMap) => this.reactMap = reactMap}
        mapStyle="mapbox://styles/mapbox/streets-v9"
        mapboxApiAccessToken="pk.eyJ1IjoieW9tYW40NyIsImEiOiJjampxM2g3YjUyMzFyM3ZvMXNjZ2R3d2ZtIn0.r0zXd8hAqa1haG3lkjlDwA"
        onViewportChange={(viewport) => this.setState({ viewport })}
        {...this.state.viewport}
      />
    );
  }
}

export default Map;
