import Layout from '../components/PageLayout';
import PlainText from '../components/PlainText';
import { TwitterTimelineEmbed, TwitterShareButton, TwitterFollowButton, TwitterHashtagButton, TwitterMentionButton, TwitterTweetEmbed, TwitterMomentShare, TwitterDMButton, TwitterVideoEmbed, TwitterOnAirButton } from 'react-twitter-embed';

const contentBacker = {
  backgroundColor:'#000000aa',
  padding: 20,
  margin: 20,
}

const Index = () => (
  <Layout>
    <div style={{textAlign:"center"}}>
        <iframe src="https://player.twitch.tv/?channel=bfbbcommunity"  frameborder="0" allowfullscreen="true" scrolling="no" height="540" width="960"></iframe>

        <div style={contentBacker}>
            <h1>Howdy!</h1>
        </div>
















        {/* This is our twitter */}
        <div style={{position:'fixed',right:20,top:70}}>
          <TwitterTimelineEmbed
            sourceType="profile"
            screenName="BfBBCommunity"
            options={{height: 800, width: 300}}
          />
          <div style={{textAlign:"left"}}>
            <TwitterFollowButton screenName={'BfBBComunity'}/>  
          </div>
          <div style={{textAlign:"right",position:"relative",top:"-24px"}}>
           <TwitterHashtagButton tag={'bfbb'}/>
          </div>
        </div>
    </div>


  </Layout>
);

export default Index;
