    return (
      <PageLayout>
        <title>The BfBB Resource Site!</title>
        <Container style={{ textAlign: "Center" }}>

          <Image id="breathing" style={{ paddingTop: 20 }} src="/bfbb-community-logo.png" fluid></Image>

          <div style={{height:"100%"}}>
