import React from 'react';
import axios from 'axios';
import dynamic from 'next/dynamic';

const DynamicComponentWithNoSSR = dynamic(() => import('../components/Map'), {
  ssr: false
});

export default class Index extends React.Component {

  getCoords = 'http://localhost:3001/taras/coordinates';


  constructor(props) {
    super(props)
    this.state = {
      coords: []
    }

    this.changeVideo = this.changeVideo.bind(this);
  }

  componentDidMount() {
    this.handleSubmit();
  }

  changeVideo = (newUrl) => {
    this.setState({ url: newUrl, playing: true })
  }

  handleSubmit = async () => {
    fetch('http://localhost:3001/taras/coordinates/geojson')
    .then(res => res.json())
    .then(data => 
      this.renderNewPoints(data.data)
    );
  }

  renderNewPoints(geojson){
    this.setState({ coords: geojson })
  }

  render() {
    return (
      <div>
        <DynamicComponentWithNoSSR />
      </div>
    )
  }
}
