import Layout from '../components/PageLayout';
import PlainText from '../components/PlainText';


const Index = () => (
  <Layout>
    <h1 id='titleCard'>Pureheart Studios</h1>
    <p id = 'subText1'>New games.</p>
    <p id = 'subText2'>Old values.</p>
    <div id='backgroundContainer'>
    </div>
    <video playsinline autoPlay muted loop id="bgvid">
        <source src="bgvid.mp4" type="video/mp4"></source>
    </video>
    <style jsx>{`

      #titleCard{
        position:absolute;
        left:-50%;
        top:50%;
        margin-left:-158px;
        animation: title-enter 1s;
        animation-delay: 2s;
        animation-fill-mode: forwards;
      }

      #subText1{
        position:absolute;
        left:-50%;
        top:90%;
        margin-left:-119px;
        animation: title-enter 1s;
        animation-delay: 0s;
        animation-fill-mode: forwards;
      }

      #subText2{
        position:absolute;
        left:-50%;
        top:90%;
        margin-left:15px;
        animation: title-enter 1s;
        animation-delay: 1s;
        animation-fill-mode: forwards;
      }

      #backgroundContainer{
        background-color: #000000;
        position: fixed;
        top:0px;
        bottom:0px;
        left:0px;
        right:0px;
        z-index: -1;
        animation-delay: 1s;
        animation: black-to-clear 2s;
        animation-fill-mode: forwards;

      }

      video#bgvid { 
        position: fixed;
        top: 50%;
        left: 50%;
        min-width: 100%;
        min-height: 100%;
        width: auto;
        height: auto;
        z-index: -100;
        -ms-transform: translateX(-50%) translateY(-50%);
        -moz-transform: translateX(-50%) translateY(-50%);
        -webkit-transform: translateX(-50%) translateY(-50%);
        transform: translateX(-50%) translateY(-50%);
        background: url(polina.jpg) no-repeat;
        background-size: cover; 
    }

      @keyframes title-enter
      {
          from {
              left: -50%;
          }
          to {
              left: 50%;
          }
      }

      @keyframes black-to-clear
      {
          from {
            background-color: #000000ff;
          }
          to {
            background-color: #00000055;
          }
      }

    `}</style>
  </Layout>
);

export default Index;
