import React from 'react';
import axios from 'axios';
import BattleShipGrid from '../components/BattleShipGrid.js'
import { Button } from 'react-bootstrap'

export default class Index extends React.Component {


  constructor(props) {
    super(props)
    this.state = {
      board: { tasks: [] },
      room: null,
      player_id: ""
    }
  }

  componentDidMount() {
    //Give the player an id
    this.setState({ player_id: [...Array(16)].map(i => (~~(Math.random() * 36)).toString(36)).join('') });

  }

  createRoomAndJoin() {
    fetch('http://localhost:3001/mainThread/createroom')
      .then(res => res.json())
      .then(data => {
        this.setState({ room: data })
      });
  }


  render() {
    return (
      <div>

        {this.state.room == null &&
          <div>
            <h1>
              Welcome to BfBB Battleship!
            </h1>

            <Button onClick={() => this.createRoomAndJoin()}>Create Room</Button>
            <InputGroup className="mb-3">
              <FormControl
                placeholder="Room ID"
                aria-label="Recipient's username"
                aria-describedby="basic-addon2"
              />
              <InputGroup.Append>
                <Button variant="outline-secondary">Button</Button>
              </InputGroup.Append>
            </InputGroup>
          </div>}
        {this.state.room != null &&
          <div>

            <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr 1fr 1fr 1fr 1fr 1fr 1fr 1fr 1fr 1fr", paddingBottom: "100%", width: 800, justifyContent: 'space-between' }}>
              {this.state.room.board1.tasks.map(task =>
                <BattleShipGrid name={task.name} type={task.type} />
              )}
            </div>
          </div>}




      </div>

    )
  }
}
